import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";

export default function History() {
  const location = useLocation();
  const navigate = useNavigate();

  // Example: get history either from location state or from localStorage
  const [orderHistory, setOrderHistory] = useState([]);

  useEffect(() => {
    if (location.state?.history) {
      setOrderHistory(location.state.history);
      // Optionally, save to localStorage
      localStorage.setItem("orderHistory", JSON.stringify(location.state.history));
    } else {
      // Load from localStorage if no state passed
      const savedHistory = localStorage.getItem("orderHistory");
      if (savedHistory) {
        setOrderHistory(JSON.parse(savedHistory));
      }
    }
  }, [location.state]);

  return (
    <div style={{ padding: "2rem", maxWidth: "800px", margin: "auto", fontFamily: "Arial, sans-serif" }}>
      <h2>ðŸ“œ Order History</h2>
      {orderHistory.length === 0 ? (
        <p>No orders yet.</p>
      ) : (
        <ul style={{ listStyle: "none", padding: 0 }}>
          {orderHistory.map((order, index) => (
            <li key={index} style={{ marginBottom: "1rem", border: "1px solid #ccc", padding: "1rem", borderRadius: "8px" }}>
              <div><strong>Order #{index + 1}</strong></div>
              <ul>
                {order.map(item => (
                  <li key={item.id}>
                    {item.name} - Qty: {item.quantity} - Price: ${(item.price * item.quantity).toFixed(2)}
                  </li>
                ))}
              </ul>
              <div style={{ fontWeight: "bold" }}>
                Total: ${order.reduce((sum, item) => sum + item.price * item.quantity, 0).toFixed(2)}
              </div>
            </li>
          ))}
        </ul>
      )}
      <button onClick={() => navigate("/coffee")} style={{ marginTop: "1rem", padding: "10px 20px" }}>
        Back Home
      </button>
    </div>
  );
}
