import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHouse } from '@fortawesome/free-solid-svg-icons';


export default function AddToCart({ cart, removeFromCart, updateQuantity }) {
  const navigate = useNavigate();

  // Flatten cart items with category included
  const allItems = Object.entries(cart).flatMap(([category, items]) =>
    Array.isArray(items) ? items.map((item) => ({ ...item, category })) : []
  );

  const [selectedItems, setSelectedItems] = useState(new Set());
  const [swipedItem, setSwipedItem] = useState(null);

  const touchStartX = useRef(0);
  const touchCurrentX = useRef(0);
  const swipingKey = useRef(null);

  const toggleItem = (key) => {
    setSelectedItems((prev) => {
      const newSet = new Set(prev);
      if (newSet.has(key)) newSet.delete(key);
      else newSet.add(key);
      return newSet;
    });
  };

  const handleQuantityChange = (item, newQty) => {
    const quantity = parseInt(newQty, 10);
    if (!isNaN(quantity) && quantity > 0) {
      updateQuantity(item.id, item.category, quantity);
    }
  };

  const selectedArray = allItems.filter((item) =>
    selectedItems.has(`${item.category}-${item.id}`)
  );

  const total = selectedArray.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  const handleOrderSelected = () => {
    if (selectedArray.length === 0) {
      alert("Please select at least one item to order.");
      return;
    }

    navigate("/payment", { state: { selectedItems: selectedArray } });

    selectedArray.forEach((item) => {
      removeFromCart(item.id, item.category);
    });
  };

  const handleGoToHistory = () => {
    navigate("/history");
  };

  // Swipe handlers
  const handleTouchStart = (e, key) => {
    touchStartX.current = e.touches ? e.touches[0].clientX : e.clientX;
    touchCurrentX.current = touchStartX.current;
    swipingKey.current = key;
  };

  const handleTouchMove = (e) => {
    if (!touchStartX.current || !swipingKey.current) return;

    touchCurrentX.current = e.touches ? e.touches[0].clientX : e.clientX;
    const diffX = touchStartX.current - touchCurrentX.current;

    if (diffX > 50) {
      setSwipedItem(swipingKey.current);
    } else if (diffX < 0) {
      setSwipedItem(null);
    }
  };

  const handleTouchEnd = () => {
    if (!touchStartX.current || !swipingKey.current) return;

    const diffX = touchStartX.current - touchCurrentX.current;
    if (diffX > 100) {
      setSwipedItem(swipingKey.current);
    } else {
      setSwipedItem(null);
    }
    touchStartX.current = 0;
    touchCurrentX.current = 0;
    swipingKey.current = null;
  };

return (
    <div
      style={{
        padding: "2rem",
        fontFamily: "'Georgia', serif",
        maxWidth: "800px",
        margin: "auto",
        backgroundColor: "#F7EFE2",
        borderRadius: "12px",
        boxShadow: "0 4px 15px rgba(0,0,0,0.1)",
      }}
    >
      <h2 style={{ marginBottom: "1.5rem", color: "#4B2E05" }}>
        ☕ Your Order History
      </h2>

      <button
        onClick={() => navigate("/coffee")}
        style={{
          backgroundColor: "#A17557",
          border: "none",
          padding: "10px 16px",
          color: "#F7EFE2",
          fontWeight: "bold",
          borderRadius: "8px",
          cursor: "pointer",
          marginBottom: "1.5rem",
          boxShadow: "0 2px 6px rgba(0,0,0,0.2)",
        }}
      >
        ← Back to Home
      </button>

      {orders.length === 0 ? (
        <p style={{ color: "#6A994E", fontStyle: "italic" }}>
          No past orders yet. Start your coffee journey!
        </p>
      ) : (
        <ul style={{ listStyle: "none", padding: 0 }}>
          {orders.map((order, idx) => (
            <li
              key={order.id || idx}
              style={{
                backgroundColor: "#FFF8F0",
                border: "1px solid #A17557",
                borderRadius: "12px",
                padding: "1rem",
                marginBottom: "1rem",
                boxShadow: "0 2px 6px rgba(161, 117, 87, 0.2)",
                color: "#4B2E05",
              }}
            >
              <div
                style={{
                  fontWeight: "bold",
                  fontSize: "1.1rem",
                  marginBottom: "0.5rem",
                  textShadow: "1px 1px 1px rgba(0,0,0,0.1)",
                }}
              >
                Order #{order.id || idx + 1} -{" "}
                <span style={{ fontWeight: "normal", fontSize: "0.9rem" }}>
                  {new Date(order.date).toLocaleDateString()}
                </span>
              </div>
              <ul style={{ paddingLeft: "1.2rem", marginBottom: "0.5rem" }}>
                {order.items.map((item) => (
                  <li key={item.id} style={{ marginBottom: "0.3rem" }}>
                    {item.quantity} x {item.name} —{" "}
                    <span style={{ fontWeight: "bold" }}>
                      ${(item.price * item.quantity).toFixed(2)}
                    </span>
                  </li>
                ))}
              </ul>
              <div
                style={{
                  fontWeight: "bold",
                  fontSize: "1rem",
                  borderTop: "1px solid #A17557",
                  paddingTop: "0.5rem",
                  textAlign: "right",
                }}
              >
                Total: ${order.items.reduce((sum, i) => sum + i.price * i.quantity, 0).toFixed(2)}
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
