import React from "react";

function CircularText({ text, radius = 120, startAngle, endAngle, invert }) {
  const characters = text.split("");
  const totalAngle = Math.abs(endAngle - startAngle);

  // Compression factor (0 = no compression, higher = more compressed)
  const compression = 0.7;

  // Helper: interpolate angles with compression
  // Map index [0..N-1] -> t in [0..1]
  // Use a quadratic easing to compress letters toward middle (t=0.5)
  function getCompressedAngle(index) {
    const N = characters.length - 1;
    let t = index / N; // normalized position from 0 to 1

    // Compress toward center using a parabola centered at 0.5
    // E.g., remap t so letters cluster near 0.5
    // Formula: mix t with 0.5 weighted by compression factor
    // Closer to 0.5 = compressed in middle
    t = t * (1 - compression) + 0.5 * compression;

    // Interpolate angle linearly between start and end using compressed t
    return startAngle < endAngle
      ? startAngle + t * totalAngle
      : startAngle - t * totalAngle;
  }

  return (
    <div
      style={{
        position: "relative",
        width: `${radius * 2}px`,
        height: `${radius * 2}px`,
      }}
    >
      {characters.map((char, index) => {
        const angle = getCompressedAngle(index);
        const rad = (angle * Math.PI) / 180;

        const x = radius + radius * Math.cos(rad);
        const y = radius - radius * Math.sin(rad);

        // Rotation for upright letters
        const rotation = invert ? angle + 270 : angle - 90;

        return (
          <span
            key={index}
            style={{
              position: "absolute",
              left: `${x}px`,
              top: `${y}px`,
              transform: `translate(-50%, -50%) rotate(${rotation}deg)`,
              fontSize: "20px",
              fontFamily: "Georgia, serif",
              color: "#5a3e36",
              userSelect: "none",
            }}
          >
            {char}
          </span>
        );
      })}
    </div>
  );
}

export default function TopAndBottomCurvedTextCompressed() {
  const radius = 120;

  return (
    <div style={{ position: "relative", width: radius * 2, height: radius * 2 }}>
      {/* Top half circle text, compressed toward middle */}
      <CircularText
        text="the daily grind"
        radius={radius}
        startAngle={180}
        endAngle={0}
        invert={false}
      />

      {/* Bottom half circle text, compressed toward middle */}
      <CircularText
        text="coffee house"
        radius={radius}
        startAngle={180}
        endAngle={360}
        invert={true}
      />
    </div>
  );
}
