import React from "react";

export default function DualText() {
  const radius = 120;
  const bottomRadius = radius * 0.85; // smaller radius for bottom text

  return (
    <div style={{ position: "relative", width: radius * 2, height: radius * 2 }}>
      {/* Top half circle text, left to right from 180° to 0° */}
      <CircularText
        text="the daily grind"
        radius={radius}
        startAngle={180}
        endAngle={0}
        invert={false}
      />

      {/* Bottom half circle text, left to right from 180° to 360° (0°) */}
      <CircularText
        text="coffee house"
        radius={bottomRadius}
        startAngle={180}
        endAngle={360}
        invert={true}
      />
    </div>
  );
}
